script:
frappe.ui.form.on('Demo', {
    refresh: function(frm) {
        set_fields_read_only(frm, true);
        frm.add_custom_button(__('Edit'), function() {
            set_fields_read_only(frm,false);
            show_id_dialog(frm); // Pass the frm object
        });
    }
});

function set_fields_read_only(frm, read_only) {
    $.each(frm.fields_dict, function(fieldname, field) {
        frm.set_df_property(fieldname, 'read_only', read_only);
    });
    frm.refresh_fields();
}

function show_id_dialog(frm) { // Receive frm object as parameter
    frappe.prompt([
        {
            'fieldname': 'id',
            'fieldtype': 'Data',
            'label': __('Enter your ID'),
            'reqd': 1
        }
    ],
    function(values){
        // Handle the entered ID here
        frappe.msgprint("Entered ID: " + values.id);
        frm.set_value('endorcement', values.id); // Set value for 'endorcement' field in the form
    },
    __('Enter ID'),
    __('Submit')
    );
}
